package typecasting;

public class Typecasting {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("The following is the example of Implicit Type Casting:");
		char c='a';
		System.out.println("The value of c:"+c);
		int n=c;
		System.out.println("The value of n:"+n);
		System.out.println("The following is the example of Explicit Type Casting:");
		int i=120;
		System.out.println("The value of i:"+i);
		char a=(char)i;
		System.out.println("The value of s:"+a);

	}

}
